import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [city, setCity] = useState('');
  const [error, setError] = useState('');

  const getWeather = async () => {
    try {
      setError('');
      const apiKey = process.env.REACT_APP_API_KEY;
      const weatherUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;
      const forecastUrl = `https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${apiKey}&units=metric`;

      const weatherResponse = await axios.get(weatherUrl);
      const forecastResponse = await axios.get(forecastUrl);

      setWeather(weatherResponse.data);

      // Filter the forecast to show only one prediction per day (around midday)
      const filteredForecast = forecastResponse.data.list.filter((entry) =>
        entry.dt_txt.includes('12:00:00')
      );
      setForecast(filteredForecast);
    } catch (err) {
      setError('City not found. Please try again.');
      setWeather(null);
      setForecast([]);
    }
  };

  return (
    <div className="app">
      <div className="search-container">
        <h1 className="title">Weather Finder</h1>
        <input
          type="text"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          placeholder="Enter city"
          className="search-input"
        />
        <button onClick={getWeather} className="search-button">Get Weather</button>
        {error && <p className="error">{error}</p>}
      </div>

      {weather && (
        <div className="weather-container">
          <h2 className="city-name">{weather.name}</h2>
          <p className="temperature">{Math.round(weather.main.temp)}°C</p>
          <p className="weather-description">{weather.weather[0].description}</p>
          <div className="weather-details">
            <p>Humidity: {weather.main.humidity}%</p>
            <p>Wind Speed: {weather.wind.speed} m/s</p>
          </div>
        </div>
      )}

      {forecast.length > 0 && (
        <div className="forecast-container">
          <h3>5-Day Forecast</h3>
          <div className="forecast">
            {forecast.map((day, index) => (
              <div key={index} className="forecast-day">
                <p>{new Date(day.dt_txt).toLocaleDateString(undefined, { weekday: 'long' })}</p>
                <p>{Math.round(day.main.temp)}°C</p>
                <p>{day.weather[0].description}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
